<script type="text/javascript" language="javascript" src="./lib/amcharts.js"></script><?php    function amchart_encode($db_dataset) {        $rv = "[";        foreach($db_dataset as $row){            $rv .= "{ ";            foreach($row as $key => $value) {                if ($key == "date") {                    $year = substr($value, 0, 4);                    if (strlen($value) > 4) {                        $month = substr($value, 5, 2);                        $rv .= "date: new Date($year, $month, 1).setDate((new Date($year, $month, 1)).getDate() - 1)";                    }                    else {                        $rv .= "date: new Date($year, 11, 31)";                    }                }                else {                    $rv .= "$key: $value";                }                $rv .= ", ";            }            $rv .= "}, ";        }        $rv .= "]";                     return $rv;    }?>