import osdef extract(src_file, output_dir):    sevenzip = os.path.abspath('./core/thirdparty/sevenzip/7z.exe')    assert os.path.isfile(sevenzip)    sevenzip_cmdline = '''{sevenzip} e {src_file} -y -o{output_dir}'''.format(        sevenzip=sevenzip, src_file=src_file, output_dir=output_dir    )    os.system(sevenzip_cmdline)